arguments.argument:
maxHp = 120;
runPower = 0.9;
weapons[0]=Rifle;
haxxyStatue = SniperHaxxyStatueS;
tauntsprite = SniperTauntS;

numFlames = 4;

// 'GW' reward - *G*olden *W*eapon
if(hasClassReward(global.paramPlayer, 'GW'))
{
    // Prepend to the start of the overlay list, so that it gets drawn first (it's a full sprite replacement)
    ds_list_insert(tauntOverlays, 0, SniperGoldWeaponTauntS);
}

if (player.team == TEAM_RED)
{
    sprite_index = SniperRedS;
    humiliationPoses = SniperRedHS;
    spriteRun = SniperRedRunS;
    spriteJump = SniperRedJumpS;
    spriteStand = SniperRedStandS;
    spriteLeanR = SniperRedLeanRS;
    spriteLeanL = SniperRedLeanLS;
    spriteIntel = SniperRedIntelS;
}
else if (player.team == TEAM_BLUE)
{
    sprite_index = SniperBlueS;
    humiliationPoses = SniperBlueHS;
    spriteRun = SniperBlueRunS;
    spriteJump = SniperBlueJumpS;
    spriteStand = SniperBlueStandS;
    spriteLeanR = SniperBlueLeanRS;
    spriteLeanL = SniperBlueLeanLS;
    spriteIntel = SniperBlueIntelS;
}
