arguments.argument:
if (cloak)
{
            inRange = false;
            with (Projectile) if (point_distance(x,y,other.x,other.y) < 100 && team != other.team) then other.inRange = true;
            with (StabMask) if (point_distance(x,y,other.x,other.y) < 50) then other.inRange = true;
            with (StabAnim) if (point_distance(x,y,other.x,other.y) < 50) then other.inRange = true;
            with (SentryTurret)
            {
                if (team != other.team && point_distance(x,y,other.x,other.y) < 375
                    && collision_line(x,y,other.x,other.y,Obstacle,true,true) < 0 ) other.inRange = true;
            }
            with (Character)
            {
                if (player.team != other.player.team && player.class == CLASS_SNIPER && abs((abs(point_direction(x,y,other.x,other.y)) - abs(aimDirection))) < 5) then other.inRange = true;
                if (other.id != id && other.team != team && point_distance(x,y,other.x,other.y) < 40) then other.inRange = true;
            }
            if (moveStatus == 2 || rocketjuggled){
            other.inRange = true;
            rocketjuggled = false;
            }
}
else inRange = false;

if(pressedKeys & $01) && taunting==false && omnomnomnom==false {
    if taunting==false && !invisible && cloakAlpha == 1 taunting=true;
    if team==TEAM_RED {
        tauntindex=0;
        tauntend=tauntlength-1;
    }
    else if team==TEAM_BLUE {
        tauntindex=tauntlength;
        tauntend=tauntlength*2-1;
    }
    image_speed=tauntspeed;
} 

if(pressedKeys & $80) && taunting==false && omnomnomnom==false{
    if(not place_free(x,y+1) && place_free(x,y)) {
        if canCloak {
            if currentWeapon.readyToStab==true {
                playsound(x,y,JumpSnd);
                vspeed = -jumpStrength;
            }
        }
        else {
            playsound(x,y,JumpSnd);
            vspeed = -jumpStrength;
        }
        if(player.class == CLASS_SPY && cloak) spyjump = true;
    }
    else if((canDoublejump == 1) and (doublejumpUsed == 0) and (vspeed>-jumpStrength)) {
        vspeed = -jumpStrength;
        playsound(x,y,JumpSnd);
        doublejumpUsed = 1;
        moveStatus = 0;
    }
}

xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0];
ysize = view_hview[0];

if(keyState & $10)  && taunting==false && omnomnomnom==false {
    if (global.isHost==true) || (global.isHost==false && distance_to_point(xoffset+xsize/2,yoffset+ysize/2) < 1600) || (player.class == CLASS_DEMOMAN)
    {
    with(currentWeapon) event_user(1);
    if(player.class == CLASS_SPY && cloak) spystab = true;
    }
} else {
    with(currentWeapon) event_user(3);
}

// Right click
if(keyState & $08) && (taunting==false || player.class == CLASS_DEMOMAN) && omnomnomnom==false{
    if (global.isHost==true) || (distance_to_point(xoffset+xsize/2,yoffset+ysize/2) < 1600) with(currentWeapon) event_user(2);
}

// Cloak
if ((pressedKeys & $08) && canCloak && ((cloakAlpha <= 0.5 and cloak) || (cloakAlpha == 1 and !cloak)) && !intel  && taunting==false && !(keyState & $10)) {
    cloak = !cloak;
}

if moveStatus == 1 speedfactor = 0.65;
else if moveStatus == 2
{
    speedfactor = 0.17;
    rocketjuggled = true;
}
else if moveStatus == 3 speedfactor = 0.1;
else speedfactor = 0.85;

if (intel) && speedfactor > 0.75 speedfactor = 0.75;

if(speedfactor<0.7) {
    if !variable_local_exists("jumpFlameParticleType") && moveStatus != 3 {
        jumpFlameParticleType = part_type_create();
        part_type_sprite(jumpFlameParticleType,FlameS,true,false,true);
        part_type_alpha2(jumpFlameParticleType,1,0.3);
        part_type_life(jumpFlameParticleType,2,5);
        part_type_scale(jumpFlameParticleType,0.7,-0.65);
    }

    if !variable_global_exists("jumpFlameParticleSystem") && moveStatus != 3 {
        global.jumpFlameParticleSystem = part_system_create();
        part_system_depth(global.jumpFlameParticleSystem, 10);
    }

    if(vspeed<0) {
        vspeed-=0.1;
        if(global.particles == PARTICLES_NORMAL) {
            if(speedfactor>0.1 and (random(10)/10)> speedfactor) {
                effect_create_below(ef_smoke,x-hspeed*1.2,y-vspeed*1.2+20,0,c_gray);
            }
        }
        if speedfactor > 0.3 {
            if(global.particles == PARTICLES_NORMAL or global.particles == PARTICLES_ALTERNATIVE) {
                  if(random(7)<5) {
                        part_particles_create(global.jumpFlameParticleSystem,x,y+19,jumpFlameParticleType,1);
                  }
            }   
        }
    } else { 
        moveStatus = 0;
        //if(variable_local_exists("jumpFlameParticleType")) {
            //part_type_destroy(jumpFlameParticleType);
        //}
    }
}

if(keyState & $40)  && taunting==false && omnomnomnom==false{
    if !canCloak hspeed -= runPower*speedfactor;
    else if currentWeapon.readyToStab==true hspeed -= runPower*speedfactor;
}

if(keyState & $20)  && taunting==false && omnomnomnom==false{   
    if !canCloak hspeed += runPower*speedfactor;
    else if currentWeapon.readyToStab==true hspeed += runPower*speedfactor;
}

if(pressedKeys & $04) {
    setChatBubble(player, 45);
}

// flame bubble
if flamecount>0 && player.class != CLASS_PYRO && random(80) <= 1 {
    setChatBubble(player, 49);
}

pressedKeys = 0;
releasedKeys = 0;

if (moveStatus = 2) {
    hspeed/=1.04;
} else {
    if (moveStatus = 3) {
        hspeed/=1.002;
    } else {
    hspeed/=1.15; 
    }
} 

if(abs(hspeed)<0.2 /*and not moveStatus = 3*/) {
    hspeed=0;
    animationImage=0;
}    

if(place_free(x,y+1)) {
    vspeed += 0.6;
    if(vspeed>10) {
        vspeed=10;
    }
    animationImage = 1;
} else {
//moveStatus = 0; 
    if(place_free(x,y)) {
        doublejumpUsed = 0;
    }
}

if(speed>15) {
    speed=15;
}

if intel { 
    if random(1)>0.90 && speed>2 {
        var sheet;
        sheet = instance_create(x,y-11+random(9),LooseSheet);
        sheet.hspeed = hspeed;
    }
    if cloak==true {
        cloak=false;
        cloakAplha=1;
    }
}

animationImage = (animationImage+abs(hspeed)/20) mod CHARACTER_ANIMATION_LEN;

if collision_point(x,y,SpawnRoom,0,0) player.quickspawn = 1;
else player.quickspawn=0;

// drop intel if ubered
if(intel) && ubered == 1 {
    event_user(5);
}

//gotta regenerate some nuts
if nutsNBolts < 100 nutsNBolts += 0.1;
else if nutsNBolts > 100 nutsNBolts = 100;

//ubered max out ammo
if ubered == 1 {
    if instance_exists(currentWeapon) {
        if currentWeapon.object_index == Rocketlauncher {
            currentWeapon.rocketcount = 4;
            currentWeapon.alarm[5] = -1;
        }
        else if currentWeapon.object_index == Scattergun {
            currentWeapon.shellCount = 6;
            currentWeapon.alarm[5] = -1;
        }
        else if currentWeapon.object_index == Flamethrower {
            currentWeapon.gas = 200;
        }
        else if currentWeapon.object_index == Shotgun {
            currentWeapon.shellCount = 8;
            currentWeapon.alarm[5] = -1;
        }
        else if currentWeapon.object_index == Revolver {
            currentWeapon.bulletCount = 6;
            currentWeapon.alarm[5] = -1;
        }
        else if currentWeapon.object_index == Minigun {
            currentWeapon.bullets = 200;
        }
        else if currentWeapon.object_index == Minegun {
            currentWeapon.mineCount = 8;
            currentWeapon.alarm[5] = -1;
        }
        else if currentWeapon.object_index == Blade {
            currentWeapon.bladePower = 100;
        }
        else if currentWeapon.object_index == Medigun {
            currentWeapon.needleCount = 40;
            currentWeapon.alarm[5] = -1;
        }
    }
}

// Determine if the character is capturing a CP, and which
var zone;
zone = collision_point(x,y,CaptureZone,0,0);

if(zone>=0 && !cloak) {
    cappingPoint = zone.cp;
} else {
    cappingPoint = noone;
}
