arguments.argument:
xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0];
ysize = view_hview[0];

if distance_to_point(xoffset+xsize/2,yoffset+ysize/2) > 800 exit;

var xr, yr;
xr = round(x);
yr = round(y);

if global.myself == -1 && cloakAlpha==1{
    image_alpha = 1;
} else if((global.myself.object == id && cloakAlpha==1) || (global.myself.object == -1)) {
    image_alpha = 1;
} else if (!invisible) {
    image_alpha = cloakAlpha;
} else {
    exit;
}

if global.myself.team == team && cloakAlpha<0.5 {
    if currentWeapon.readyToStab==true{
        cloakAlpha=0.5;
        image_alpha=0.5;
    }
}

if team == global.myself.team && (id != global.myself.object.id || global.showHealthBar == 1){
    draw_set_alpha(1);
    draw_healthbar(xr-10, yr-30, xr+10, yr-25,hp*100/maxHp,c_black,c_red,c_green,0,true,true);
}    
if(distance_to_point(mouse_x, mouse_y)<25) {
    if cloak && team!=global.myself.team exit;
    draw_set_alpha(1);
    draw_set_halign(fa_center);
    draw_set_valign(fa_bottom);
    if(team==TEAM_RED) {
        draw_set_color(c_red);
    } else {
        draw_set_color(c_blue);
    }
draw_text(xr, yr-35, player.name);
}  

draw_set_alpha(1);
if team == TEAM_RED ubercolour = c_red;
if team == TEAM_BLUE ubercolour = c_blue;

var hatSuffix;
switch(player.hat) {
    case 1:
        hatSuffix = "X";
        break;
    case 2:
        hatSuffix = "P";
        break;
    default:
        hatSuffix = "";
}

if omnomnomnom == true {
    var hatOmnomnomSprite;
    hatOmnomnomSprite = execute_string("return OmnomnomnomS"+hatSuffix+";");
    draw_sprite_ext(hatOmnomnomSprite,omnomnomnomindex,xr,yr,image_xscale,image_yscale,image_angle,c_white,1);
    if ubered == 1 draw_sprite_ext(hatOmnomnomSprite,omnomnomnomindex,xr,yr,image_xscale,image_yscale,image_angle,ubercolour,0.7);
} else if taunting == true{
    var hatTauntSprite;
    hatTauntSprite = execute_string("return "+sprite_get_name(tauntsprite)+hatSuffix+";");
    draw_sprite_ext(hatTauntSprite,tauntindex,xr,yr,image_xscale,image_yscale,image_angle,c_white,1);
    if ubered == 1 draw_sprite_ext(hatTauntSprite,tauntindex,xr,yr,image_xscale,image_yscale,image_angle,ubercolour,0.7);
} else if taunting == false {
    var hatSprite;
    hatSprite = execute_string("return "+sprite_get_name(sprite_index)+hatSuffix+";");
    if ubered == 0 draw_sprite_ext(hatSprite,floor(animationImage+animationOffset),xr,yr,image_xscale,image_yscale,image_angle,c_white,cloakAlpha);
    else if ubered == 1 {
        draw_sprite_ext(hatSprite,floor(animationImage+animationOffset),xr,yr,image_xscale,image_yscale,image_angle,c_white,1);
        draw_sprite_ext(hatSprite,floor(animationImage+animationOffset),xr,yr,image_xscale,image_yscale,image_angle,ubercolour,0.7);
    }
}
