arguments.argument:
if(instance_exists(InGameMenuController) || instance_exists(OptionsController) || instance_exists(ControlsController) 
|| instance_exists(HUDOptionsController)) || (instance_exists(TeamSelectController) || instance_exists(ClassSelectController))
{
    exit;
}

if isTyping
{
    // only print something if we've typed it
    if keyboard_string != ""
    {
        var str;
        str = keyboard_string;
        if string_count("#", str) > 0
        {
            str = "No hashes allowed";
        }

        write_ubyte(global.serverSocket, isTyping);// Will be either CHAT_PUBLIC_MESSAGE or CHAT_PRIV_MESSAGE
        write_ubyte(global.serverSocket, string_length(str));
        write_string(global.serverSocket, str);
        socket_send(global.serverSocket);
    }

    isTyping = false;
}

keyboard_string = "";
alarm[0] = -1
alarm[1] = 60;
blinking_underscore = false;
