arguments.argument:
if instance_exists(InGameMenuController) || instance_exists(OptionsController) ||
    instance_exists(ControlsController) || instance_exists(HUDOptionsController) exit;
if instance_exists(TeamSelectController) || instance_exists(ClassSelectController)
    menuOpen = true;
    else menuOpen = false;

io_handle();

//Checking for input - Mapped Keys
if keyboard_check_pressed(global.changeTeam) inputChangeTeam();
if keyboard_check_pressed(global.changeClass) inputChangeClass();
if(keyboard_check_direct(global.showScores))
    inputShowScores();
else
    inputCloseScores();

var keybyte;
keybyte = 0;

//character object exists
if(global.myself.object != -1) {
    if(!menuOpen) {
        if keyboard_check(global.left) keybyte |= $40;
        if keyboard_check(global.right) keybyte |= $20;
        if keyboard_check(global.jump) keybyte |= $80;
        if keyboard_check(global.attack) && global.myself.humiliated == 0 keybyte |= $10;
        if keyboard_check(global.special) && global.myself.humiliated == 0 keybyte |= $08;
        if keyboard_check_pressed(global.medic) keybyte |= $04;
        if keyboard_check_pressed(global.taunt)
        {
            keybyte |= $01;
            inputTaunt();
        }
        if keyboard_check_pressed(global.special) && global.myself.humiliated == 0 inputSpecial();
        if keyboard_check_pressed(global.chat1) inputChat1();
        if keyboard_check_pressed(global.chat2) inputChat2();
        if keyboard_check_pressed(global.chat3) inputChat3();
        if keyboard_check_pressed(global.drop) inputDrop();

        if(mouse_check_button(mb_left)) {
            if global.attack == MOUSE_LEFT && global.myself.humiliated == 0 keybyte |= $10;
            else if global.special == MOUSE_LEFT && global.myself.humiliated == 0 {
                keybyte |= $08;
                inputSpecial();
            }
        }
        if mouse_check_button(mb_right) {
            if global.attack == MOUSE_RIGHT && global.myself.humiliated == 0 keybyte |= $10;
            else if global.special == MOUSE_RIGHT && global.myself.humiliated == 0 {
                keybyte |= $08;
                inputSpecial();
            }
        }
    }

    ClientInputstate(global.serverSocket, keybyte);
    socket_send(global.serverSocket);
}

if(keybyte != 0
        or keyboard_check(global.left)
        or keyboard_check(global.right)
        or keyboard_check(global.jump)
        or keyboard_check(global.down)) {
    afktimer=afktimeout;
}
