arguments.argument:
xviewoffset = view_xview[0];
yviewoffset = view_yview[0];
xviewsize = view_wview[0];
yviewsize = view_hview[0];

if (distance_to_point(xviewoffset+xviewsize/2,yviewoffset+yviewsize/2) > 800) {
    //reset the animation
    alarm[6] = 0;
    exit;
}


var imageOffset;
    if(justShot == true) {
        imageOffset=2;
        justShot = false;
    } else {
        imageOffset=0;
    }

if (alarm[6] <= 0){
    //if we are not shooting or recoiling
    if (object_index != SentryTurret){    
        imageOffset += owner.team;
    }
}else{
    //Play the current animation normally
    if (object_index != Rifle) {
        imageOffset = floor(image_index mod recoilAnimLength) + recoilAnimLength*owner.team
    }else{
        if (sprite_index == recoilSprite)
            imageOffset = floor(image_index mod recoilAnimLength) + recoilAnimLength*owner.team
        else 
            imageOffset = floor(image_index mod reloadAnimLength) + reloadAnimLength*owner.team
    }
}

if object_index != SentryTurret {    
    if (!owner.invisible) && (!owner.taunting) && owner.cloakAlpha > 0.5 && !owner.omnomnomnom && !owner.player.humiliated {
        draw_sprite_ext(sprite_index,imageOffset,round(x+xoffset*image_xscale),round(y+yoffset),image_xscale,image_yscale,image_angle,c_white,owner.cloakAlpha);
        if owner.ubered == 1 {
            if owner.team == TEAM_RED ubercolour = c_red;
            else if owner.team == TEAM_BLUE ubercolour = c_blue;
            draw_sprite_ext(sprite_index,imageOffset,round(x+xoffset*image_xscale),round(y+yoffset),image_xscale,image_yscale,image_angle,ubercolour,0.7);
        }
    }
} else {             
    if(ownerPlayer.sentry) {
        if(ownerPlayer.sentry.image_index > 7) {
            if(!rotating) {
                draw_sprite_ext(sprite_index,ownerPlayer.team + imageOffset,round(x+xoffset*image_xscale),round(y+yoffset),image_xscale,image_yscale,image_angle,c_white,1);
            } else {
                draw_sprite_ext(TurretRotateS,round(rotateindex),x,y,image_xscale,image_yscale,0,c_white,1);
            }
        }
    }
}
