arguments.argument:
if (text == "") {
    // if not displaying console in in-game menu
    if (global.consoleMode != CONSOLE_INMENU) {
        instance_destroy();
    }
} else {
    ConsolePrint(">" + text);

    var command, args;
    if (string_pos(" ", text) != 0) {
        command = string_copy(text, 0, string_pos(" ", text) - 1);
        args = string_copy(text, string_pos(" ",text) + 1, string_length(text) - string_pos(" ", text));
    } else {
        command = text;
        args = "";
    }

    if (ConsoleRunCommand(command, args) == false) {
        ConsolePrint("Unknown command '" + command + "', try help");
    }

    ds_list_add(global.ConsoleCmdLog, text);
    while (ds_list_size(global.ConsoleCmdLog) > 10) {
        ds_list_delete(global.ConsoleCmdLog, 0);
    }
    text = "";
    keyboard_string = "_";
    _ConsoleDoSeek(0);
}
