arguments.argument:
x = view_xview[0] + global.ConsoleWindowX;
y = view_yview[0] + global.ConsoleWindowY;

// drag console
if (x <= mouse_x && mouse_x < x + global.ConsoleWidth && y - console_height <= mouse_y && mouse_y < y) {
    dragging = true;
    dragstart_mousex = mouse_x;
    dragstart_mousey = mouse_y;
    dragstart_winx = global.ConsoleWindowX;
    dragstart_winy = global.ConsoleWindowY;
// left border resize
} else if (x - padding <= mouse_x && mouse_x < x && y - console_height <= mouse_y && mouse_y < y) {
    resizing_x1 = true;
    resizestart_mousex = mouse_x;
    resizestart_x = global.ConsoleWindowX;
    resizestart_width = global.ConsoleWidth;
// right border resize
} else if (x + global.ConsoleWidth <= mouse_x && mouse_x < x + global.ConsoleWidth + padding && y - console_height <= mouse_y && mouse_y < y) {
    resizing_x2 = true;
    resizestart_mousex = mouse_x;
    resizestart_width = global.ConsoleWidth;
}
