arguments.argument:
fade -= 1/90 * global.delta_factor;
if (fade < 0)
{
    fade = 0;
}

if (global.backgroundHash != "default")
{
    // hovering over title
    if (mouse_x >= 595 - string_width(global.backgroundTitle)
        && mouse_x < 595
        && mouse_y >= 595 - string_height(global.backgroundTitle)
        && mouse_y < 595
        && global.backgroundURL != "")
    {
        // clicking
        if (mouse_check_button_pressed(mb_left))
        {
            mouse_clear(mb_left);
            action_splash_web(global.backgroundURL, 1);
        }


    }
    // open console
    if(keyboard_check_pressed(global.openConsole)) inputOpenMainMenuConsole();
}
