arguments.argument:
{
    global.levelchoice = 1
    menu_create(self, 40, 300, 200, 200, 30);
    menu_addlink(self, "Host Game", 'event_user(0);');
    menu_addlink(self, "Join (lobby)", '
        room_goto_fix(Lobby);
    ');
    menu_addlink(self, "Join (manual)", '
        global.isHost = false;
        global.serverIP = get_string("Please enter the IP address or hostname of the server you want to join", "127.0.0.1");
        global.serverPort = get_integer("Please enter the port of the server you want to join", 8190);
        instance_create(0,0,Client);
        Client.returnRoom = Menu;
    ');
    menu_addlink(self, "Options", '
        room_goto_fix(Options);
    ');
    menu_addlink(self, "Credits", '
        room_goto_fix(Credits);
    ');
    menu_addlink(self, "Quit", '
        game_end();
    ');

    if !sound_isplaying(global.MenuMusic) AudioControlPlaySong(global.MenuMusic);
    menuimage=choose(0,1);

    if(global.dedicatedMode == 1)
        event_user(0);
}
