arguments.argument:
if(!variable_local_exists("baseybegin"))
    baseybegin = ybegin;
if(view_hview[0] < 600)
    ybegin = round(baseybegin
                   - min(1, max(0, window_mouse_get_y()/view_hview[0]))
                     *(600-view_hview[0]));
stepHasRun = true;
if(editing != -1) {
    switch (item_type[editing]) {
        case "edittext2":
        case "edittext":
        case "editnum":
            item_value[editing] = keyboard_string;
            break;
        case "editkeyormouse":
            if mouse_check_button(mb_left) {
                menu_set_var(editing, MOUSE_LEFT);
                execute_string(item_script[editing], MOUSE_LEFT);
                editing = -1;
                exit;
            }
            else if mouse_check_button(mb_right) {
                menu_set_var(editing, MOUSE_RIGHT);
                execute_string(item_script[editing], MOUSE_RIGHT);
                editing = -1;
                exit;
            }
        case "editkey":
            if keyboard_check_direct(vk_tab) keyboard_lastkey = vk_tab;
            if keyboard_lastkey != 0 {
                menu_set_var(editing, keyboard_lastkey);
                execute_string(item_script[editing], menu_get_var(editing));
                editing = -1;
                exit;
            }
    }
} else if(mouse_x > view_xview[0]+xbegin) {
    if(mouse_y >= view_yview[0]+ybegin-(spacing/2) and mouse_x < view_xview[0]+xbegin+width)
    {
        virtualitem = max(0, round((mouse_y-(view_yview[0]+ybegin))/spacing));
        if(mouse_check_button_pressed(mb_left)){
            mouse_clear(mb_left);
            if(virtualitem < items) {
                switch (item_type[virtualitem]) {
                    case "script":
                        execute_string(item_script[virtualitem]);
                        break;
                    case "editkey":
                    case "editkeyormouse":
                        io_clear();
                        editing = virtualitem;
                        break;
                    case "edittext":
                    case "edittext2":
                    case "editnum":
                        keyboard_string = "";
                        editing = virtualitem;
                        break;
                    case "editselect":
                        item_value[virtualitem] = (item_value[virtualitem]+1) mod item_options[virtualitem];
                        menu_set_var(virtualitem, item_option_value[virtualitem, item_value[virtualitem]]);
                        execute_string(item_script[virtualitem], menu_get_var(virtualitem));
                        break;
                }
            }
        }
    }
    else if(mouse_y < view_yview[0]+ybegin-taboffset+spacing/2 and mouse_y > view_yview[0]+ybegin-taboffset-spacing/2
            and mouse_x > view_xview[0]+xbegin+tabmargin and mouse_x < view_xview[0]+xbegin+tabmargin+tabspacing*tabs)
    {
        virtualitem = min(-2, -2-floor((mouse_x-(view_xview[0]+xbegin+tabmargin))/tabspacing));
        if(mouse_check_button_pressed(mb_left))
        {
            mouse_clear(mb_left);
            if(-2-virtualitem < tabs)
            {
                execute_string(tab_script[-2-virtualitem]);
            }
        }
    } else {
        virtualitem = -1;
    }
} else {
    virtualitem = -1;
}
