arguments.argument:
{
    menu_create(48, 172, 500, 260, 32, 40, 92, 128);
    menumode = true;

    if (room != Options)
        menu_setdimmed();

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    menu_addlink("Basic", '
        instance_create(x,y,ControlsController);
        instance_destroy();
    ');
    menu_addedit_keyormouse("Primary Fire:", "global.attack");
    menu_addedit_keyormouse("Secondary Ability:", "global.special");
    menu_addedit_key("Emotes 1:", "global.chat1");
    menu_addedit_key("Emotes 2:", "global.chat2");
    menu_addedit_key("Emotes 3:", "global.chat3");
    menu_addedit_key("Change Team:", "global.changeTeam");
    menu_addedit_key("Change Class:", "global.changeClass");

    menu_addtablink("Ingame", '
        instance_create(x,y,OptionsController);
        instance_destroy();
    ');
    menu_addtablink("Engine", '
        instance_create(x,y,EngineOptionsController);
        instance_destroy();
    ');
    menu_addtablink("Controls", '
        instance_create(x,y,ControlsController);
        instance_destroy();
    ');
    menu_addtablink("HUD", '
        instance_create(x,y,HUDOptionsController);
        instance_destroy();
    ');

    menu_addback("Back", '
        instance_destroy();
        if(room == Options)
            room_goto_fix(Menu);
        else
            instance_create(0,0,InGameMenuController);
    ');
