arguments.argument:
{
    global.fullscreen = not global.fullscreen;

    ini_open("gg2.ini");
    ini_write_real("Settings", "Fullscreen", global.fullscreen);
    ini_close();

    if(global.fullscreen) {
        value[1] = "On";
    } else {
        value[1] = "Off";
    }

    window_set_fullscreen(global.fullscreen);
}
