arguments.argument:
{
    if global.monitorSync == 1 global.monitorSync = 0;
    else global.monitorSync = 1;

    ini_open("gg2.ini");
    ini_write_real("Settings", "Monitor Sync", global.monitorSync);
    ini_close();

    if(global.monitorSync == 0) {
        set_synchronization(false);
        value[10] = "Disabled";
    } else {
        set_synchronization(true);
        value[10] = "Enabled";
    }
}
