arguments.argument:
{
    if global.showHealthBar == 1 global.showHealthBar = 0;
    else global.showHealthBar = 1;

    ini_open("gg2.ini");
    ini_write_real("Settings", "Show Healthbar", global.showHealthBar);
    ini_close();

    if(global.showHealthBar == 0) {
        value[8] = "Disabled";
    } else {
        value[8] = "Enabled";
    }
}
