arguments.argument:
{
    if(!game_init())
    {
        game_end();
        exit;
    }
    alarm[0] = 30;
    alarm[1] = 240;
    // Music setting set to "Menu Only" or "Menu and In-Game"
    if (global.music == MUSIC_MENU_ONLY || global.music == MUSIC_BOTH)
    {
        AudioControlPlaySong(global.FaucetMusic);
    }
    image_speed=0;
}
