arguments.argument:
xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0];
ysize = view_hview[0];

if instance_exists(WinBanner) with WinBanner instance_destroy();

draw_set_color(c_white);
draw_set_valign(fa_center);
draw_set_alpha(1);

//CP
if ArenaControlPoint.team == TEAM_RED teamoffset = 60;
else if ArenaControlPoint.team == TEAM_BLUE teamoffset = 90;
if ArenaControlPoint.cappingTeam == TEAM_RED && ArenaControlPoint.team == -1 teamoffset = 30;
else if ArenaControlPoint.cappingTeam != TEAM_RED && ArenaControlPoint.team == -1 teamoffset = 0;    
if ArenaControlPoint.capping != 0 draw_sprite_ext(ControlPointStatusS, teamoffset+floor(ArenaControlPoint.capping/ArenaControlPoint.capTime*30), xoffset+xsize/2, yoffset+560, 3, 3, 0, c_white, 1);
else if ArenaControlPoint.capping == 0 draw_sprite_ext(ControlPointStatusS, teamoffset, xoffset+xsize/2, yoffset+560, 3, 3, 0, c_white, 1);
if ArenaControlPoint.locked == 1 {
    draw_set_halign(fa_center);
    if cpUnlock >= 150 draw_sprite_ext(ControlPointLockS, 0, xoffset+xsize/2, yoffset+560, 3, 3, 0, c_white, 1);
    else if cpUnlock >= 30 draw_text_transformed(xoffset+xsize/2+2, yoffset+562, ceil(cpUnlock/30), 3,3, 0);
}
else if ArenaControlPoint.cappers > 0 && ArenaControlPoint.locked == 0{
    draw_sprite_ext(ControlPointCappersS, 0, xoffset+xsize/2, yoffset+560, 3, 3, 0, c_white, 1);
    draw_set_halign(fa_center);
    draw_set_color(c_black);
    draw_text_transformed(xoffset+xsize/2+13, yoffset+563, string(ArenaControlPoint.cappers),1.5,1.5,0);
}

//timer
draw_set_color(c_white);
if global.myself != -1 {
    if global.myself.team == TEAM_RED teamoffset=0;
    else if global.myself.team == TEAM_BLUE teamoffset=1;
}
if overtime == 1 {
    draw_set_halign(fa_center);
    draw_sprite_ext(TimerHudS,2+teamoffset,xoffset+xsize/2+xshift,yoffset+30+ yshift,3,3,0,c_white,1);
    draw_text(xoffset+xsize/2+xshift,yoffset+30+ yshift,"OVERTIME");
}
else{
    draw_set_halign(fa_right);
    draw_sprite_ext(TimerHudS,teamoffset,xoffset+xsize/2 +xshift,yoffset+30+ yshift,3,3,0,c_white,1);
    draw_sprite_ext(TimerS,floor(timer/timeLimit*12),xoffset+xsize/2+39 +xshift,yoffset+30+ yshift,3,3,0,c_white,1);
    minutes=floor(timer/1800);
    secondcounter=timer-minutes*1800;
    seconds=floor(secondcounter/30);    
    draw_text_transformed((xoffset+xsize/2-1)-10+xshift,yoffset+32+ yshift,string(minutes),1.5,1.5,0);
    draw_text_transformed((xoffset+xsize/2)+xshift,yoffset+32+ yshift,":",1.5,1.5,0);
    if seconds >= 10 draw_text_transformed((xoffset+xsize/2+20)+xshift,yoffset+32+ yshift,string(seconds),1.5,1.5,0);
    else if seconds < 10 draw_text_transformed((xoffset+xsize/2+20)+xshift,yoffset+32+ yshift,"0"+string(seconds),1.5,1.5,0);
}

draw_set_halign(fa_center); 
if roundStart > 0 {
    draw_text(xoffset+xsize/2,yoffset+ysize/2,"Game will start in "+string(floor(roundStart/30))+" seconds")
}

if global.myself != -1 {
    if global.myself.object == -1 && roundStart == 0 && winners == TEAM_SPECTATOR && global.winners == -1 && endCount == 0 && !instance_exists(DeathCam) && global.myself.team !=TEAM_SPECTATOR{
        draw_text(xoffset+xsize/2,yoffset+ysize/2,"You can spawn when the next round starts")
    }
    //Alive players remaining 
    draw_set_color(c_black);
    draw_sprite_ext(ArenaPlayerCountS,0,xoffset+xsize/2,yoffset+71,2,2,0,c_white,1);
    draw_text(xoffset+xsize/2+15,yoffset+73,redteamCharacters);
    draw_sprite_ext(ArenaPlayerCountS,1,xoffset+xsize/2,yoffset+104,2,2,0,c_white,1);
    draw_text(xoffset+xsize/2+15,yoffset+106,blueteamCharacters);
}

//MVPs
if endCount > 0 && global.winners == -1 && !instance_exists(ScoreTableController){
    if instance_exists(DeathCam) {
        with DeathCam instance_destroy();
    }

    draw_set_alpha(1);    
    draw_set_halign(fa_left);
    draw_set_color(c_white);

    offset = 15;
    if winners == TEAM_RED {
        if endCount < 150 {
            draw_sprite_ext(MVPBannerS,0,xoffset+xsize/2,yoffset+ysize/2,2,2,0,c_white,0.8);
            for (i=0; i<redMVPs; i+=1) {
                draw_text(xoffset+xsize/2-250,yoffset+ysize/2+76+offset*i,redMVPName[i]);
                draw_text(xoffset+xsize/2-30,yoffset+ysize/2+76+offset*i,redMVPKills[i]);
                draw_text(xoffset+xsize/2+122,yoffset+ysize/2+76+offset*i,redMVPHealed[i]);
                draw_text(xoffset+xsize/2+248,yoffset+ysize/2+76+offset*i,redMVPPoints[i]);
            }
        }
        else if endCount >= 150 {
            draw_sprite_ext(MVPBannerS,1,xoffset+xsize/2,yoffset+ysize/2,2,2,0,c_white,0.8);
            for (i=0; i<blueMVPs; i+=1) {
                draw_text(xoffset+xsize/2-250,yoffset+ysize/2+76+offset*i,blueMVPName[i]);
                draw_text(xoffset+xsize/2-30,yoffset+ysize/2+76+offset*i,blueMVPKills[i]);
                draw_text(xoffset+xsize/2+122,yoffset+ysize/2+76+offset*i,blueMVPHealed[i]);
                draw_text(xoffset+xsize/2+248,yoffset+ysize/2+76+offset*i,blueMVPPoints[i]);
            }
        }
    }
    else if winners == TEAM_BLUE {
        if endCount < 150 {
            draw_sprite_ext(MVPBannerS,2,xoffset+xsize/2,yoffset+ysize/2,2,2,0,c_white,0.8);
            for (i=0; i<blueMVPs; i+=1) {
                draw_text(xoffset+xsize/2-250,yoffset+ysize/2+76+offset*i,blueMVPName[i]);
                draw_text(xoffset+xsize/2-30,yoffset+ysize/2+76+offset*i,blueMVPKills[i]);
                draw_text(xoffset+xsize/2+122,yoffset+ysize/2+76+offset*i,blueMVPHealed[i]);
                draw_text(xoffset+xsize/2+248,yoffset+ysize/2+76+offset*i,blueMVPPoints[i]);
            }
        }
        else if endCount >= 150 {
            draw_sprite_ext(MVPBannerS,3,xoffset+xsize/2,yoffset+ysize/2,2,2,0,c_white,0.8);
            for (i=0; i<redMVPs; i+=1) {
                draw_text(xoffset+xsize/2-250,yoffset+ysize/2+76+offset*i,redMVPName[i]);
                draw_text(xoffset+xsize/2-30,yoffset+ysize/2+76+offset*i,redMVPKills[i]);
                draw_text(xoffset+xsize/2+122,yoffset+ysize/2+76+offset*i,redMVPHealed[i]);
                draw_text(xoffset+xsize/2+248,yoffset+ysize/2+76+offset*i,redMVPPoints[i]);
            }
        }
    }
    draw_set_color(make_color_rgb(227,226,225));
    draw_set_halign(fa_right);
    draw_text_transformed(xoffset+384,yoffset+210,redWins,3.5,3.5,0);
    draw_set_halign(fa_left);
    draw_text_transformed(xoffset+415,yoffset+210,blueWins,3.5,3.5,0);

}
