arguments.argument:
//start game if there are 2 or more players and
//check for last player on a team then restart arena

redteam = 0;
blueteam = 0;
redteamCharacters = 0;
blueteamCharacters = 0;

for(i = 0; i < ds_list_size(global.players); i += 1)
{
    player = ds_list_find_value(global.players, i);
    if(player.team == TEAM_RED)
    {
        redteam += 1;
        if (player.object != -1 or alarm[5] != -1)
            redteamCharacters += 1;
    }
    else if (player.team == TEAM_BLUE)
    {
        blueteam += 1;
        if (player.object != -1 or alarm[5] != -1)
            blueteamCharacters += 1;    
    }
}

if (global.run_virtual_ticks)
{
    if (global.isHost)
    {
        if (ArenaControlPoint.team != -1)
            winners = ArenaControlPoint.team;
        if (cpUnlock <= 0)
        {
            sendEventUnlockCP();
            doEventUnlockCP();
        }
        else if (cpUnlock == 150)
            GameServer.syncTimer = 1;

        if ((blueteamCharacters == 0 or redteamCharacters == 0) and roundStart == 0)
        {
            if ((redteam >= 1 or blueteam >= 1) and winners == TEAM_SPECTATOR)
            {
                if (blueteamCharacters == 0 and gameStarted == 1)
                    winners = TEAM_RED;
                else if (redteamCharacters == 0 and gameStarted == 1)
                    winners = TEAM_BLUE;
                else
                {
                    winners = TEAM_SPECTATOR;
                    if (redteam >= 1 and blueteam >=1 and gameStarted == 0)
                    {
                        serverArenaRestart();
                        gameStarted = 1;
                    }
                }
            }
        }
        if (winners != TEAM_SPECTATOR)
        {
            endCount += 1;
            if (endCount == 300 and overtime == 0 and timer != 0)
            {
                endCount = 0;
                if (redteam == 0 or blueteam == 0)
                {
                    gameStarted = 0;
                    winners = TEAM_SPECTATOR;
                    with (Player)
                    {
                        if (object != -1)
                        {
                            if (id == global.myself and !instance_exists(Spectator))
                            {
                                instance_create(object.x,object.y,Spectator);
                                with object instance_destroy();
                            }
                        }
                    }
                    with (Sentry)
                        instance_destroy();
                    with (SentryGibs)
                        instance_destroy();
                }
                else serverArenaRestart();
            }
            else if (endCount == 1)
            {
                serverArenaEndRound();
            }
        }
    }
    //else if client, increase endcount once server sends the EndRound message
    else
    {
        if (endCount > 0)
            endCount += 1;
        if (endCount == 300)
            endCount = 0;
        //also check number of red/blue players to set winners as spec
        if (winners != TEAM_SPECTATOR and endCount == 0
            and (redteam == 0 or blueteam == 0))
            winners = TEAM_SPECTATOR;
    }
}

if (roundStart == 0)
    cpUnlock -= 1 * global.delta_factor;

//when roundStart reaches 0 players can no longer change team/class
//and respawn
else if (roundStart > 0)
    roundStart -= 1 * global.delta_factor;
if (roundStart == 1)
{
    if (global.isHost)
    {
        sendEventArenaStartRound();
        doEventArenaStartRound();
        GameServer.syncTimer = 1;
    }
}

//timer
if (timer > 0)
    timer -= 1 * global.delta_factor;

if (timer == 0)
{
    if (redteamCharacters > 0 and blueteamCharacters > 0 and redteam > 0 and blueteam > 0 and winners == TEAM_SPECTATOR)
        overtime = 1;
    else if endCount == 300 or gameStarted == 0
        global.winners = TEAM_SPECTATOR;
}
