arguments.argument:
xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0];
ysize = view_hview[0];

draw_set_color(c_white);
draw_set_valign(fa_center);
draw_set_alpha(1);

//CP
if KothControlPoint.team == TEAM_RED teamoffset = 60;
else if KothControlPoint.team == TEAM_BLUE teamoffset = 90;
if KothControlPoint.cappingTeam == TEAM_RED && KothControlPoint.team == -1 teamoffset = 30;
else if KothControlPoint.cappingTeam != TEAM_RED && KothControlPoint.team == -1 teamoffset = 0;    
if KothControlPoint.capping != 0 draw_sprite_ext(ControlPointStatusS, teamoffset+floor(KothControlPoint.capping/KothControlPoint.capTime*30), xoffset+xsize/2, yoffset+560, 3, 3, 0, c_white, 1);
else if KothControlPoint.capping == 0 draw_sprite_ext(ControlPointStatusS, teamoffset, xoffset+xsize/2, yoffset+560, 3, 3, 0, c_white, 1);
if KothControlPoint.locked == 1 {
    draw_set_halign(fa_center);
    if cpUnlock >= 150 draw_sprite_ext(ControlPointLockS, 0, xoffset+xsize/2, yoffset+560, 3, 3, 0, c_white, 1);
    else if cpUnlock >= 30 draw_text_transformed(xoffset+xsize/2+2, yoffset+562, ceil(cpUnlock/30), 3,3, 0);
}
else if KothControlPoint.cappers > 0 && KothControlPoint.locked == 0{
    draw_sprite_ext(ControlPointCappersS, 0, xoffset+xsize/2, yoffset+560, 3, 3, 0, c_white, 1);
    draw_set_halign(fa_center);
    draw_set_color(c_black);
    draw_text_transformed(xoffset+xsize/2+13, yoffset+563, string(KothControlPoint.cappers),1.5,1.5,0);
}

if instance_exists(WinBanner) exit;
draw_set_color(c_white);
//timer highlight
if KothControlPoint.team != -1 {
    if KothControlPoint.team == TEAM_RED {
        draw_sprite_ext(TimerOutlineS, 0, xoffset+xsize/2-coffset+xshift, yoffset+36, 2,2,0,c_white,1);
    }
    else if KothControlPoint.team == TEAM_BLUE {
        draw_sprite_ext(TimerOutlineS, 0, xoffset+xsize/2+coffset+xshift, yoffset+36+ yshift, 2,2,0,c_white,1);
    }
}
//red timer HUD
draw_sprite_ext(TimerHudS, 2, xoffset+xsize/2-coffset+xshift, yoffset+36, 2,2,0,c_white,1);
if (redTimer > 0) {
    redMinutes=floor(redTimer/1800);
    redSecondcounter=redTimer-redMinutes*1800;
    redSeconds=floor(redSecondcounter/30);
    draw_set_halign(fa_right);
    draw_text_transformed(xoffset+xsize/2-10-coffset+xshift,yoffset+38,string(redMinutes),1.5,1.5,0);
    draw_text_transformed(xoffset+xsize/2-coffset+xshift, yoffset+38,":", 1.5,1.5,0);
    if redSeconds >= 10 draw_text_transformed(xoffset+xsize/2+20-coffset+xshift,yoffset+38,string(redSeconds),1.5,1.5,0);
        else if redSeconds < 10 draw_text_transformed(xoffset+xsize/2+20-coffset+xshift,yoffset+38,"0"+string(redSeconds),1.5,1.5,0);
}
else
{
    draw_set_halign(fa_center);
    draw_text(xoffset+xsize/2-coffset+xshift,yoffset+36,"OVERTIME");
}
//blue timer HUD    
draw_sprite_ext(TimerHudS, 3, xoffset+xsize/2+coffset+xshift, yoffset+36+yshift, 2,2,0,c_white,1);
if (blueTimer >0) {
    blueMinutes=floor(blueTimer/1800);
    blueSecondcounter=blueTimer-blueMinutes*1800;
    blueSeconds=floor(blueSecondcounter/30);
    draw_set_halign(fa_right);
    draw_text_transformed(xoffset+xsize/2-10+coffset+xshift,yoffset+38+yshift,string(blueMinutes),1.5,1.5,0);
    draw_text_transformed(xoffset+xsize/2+coffset+xshift, yoffset+38+yshift,":", 1.5,1.5,0);
    if blueSeconds >= 10 draw_text_transformed(xoffset+xsize/2+20+coffset+xshift,yoffset+38+yshift,string(blueSeconds),1.5,1.5,0);
        else if blueSeconds < 10 draw_text_transformed(xoffset+xsize/2+20+coffset+xshift,yoffset+38+yshift,"0"+string(blueSeconds),1.5,1.5,0);
}
else 
{
    draw_set_halign(fa_center);
    draw_text(xoffset+xsize/2+coffset+xshift,yoffset+36+yshift,"OVERTIME");
}
