arguments.argument:
if(global.winners == TEAM_RED) {
    teamoffset = 0;
} else if(global.winners == TEAM_BLUE) {
    teamoffset = 1;
} else {
    teamoffset = 2;
}

if(global.myself.team != global.winners) {
    FailureSnd = faudio_new_generator(FailureSndS);
    sound=FailureSnd;
    global.myself.humiliated = 1;
} else {
    VictorySnd = faudio_new_generator(VictorySndS);
    sound = VictorySnd;
}

with (Player) {
    if team == global.winners
        humiliated = 0;
    else {
        humiliated = 1;
    }
}

if(!instance_exists(ArenaHUD)) {
    AudioControlPlaySong(sound, false);
}
