arguments.argument:
{
    if(other.id != ownerPlayer.object and other.team != team)
    {
        if(!other.ubered)
        { 
            projectileCollision(ownerPlayer, other, hitDamage, id, 25);
            other.timeUnscathed = 0;
        }
        with(other)
            motion_add(other.direction, other.speed*0.4);
        dealFlicker(other.id);

        instance_destroy();
    }
}
