arguments.argument:
if direction >= 95 && direction <=270{ 
    image_xscale=-1;
}else{ 
    image_xscale = 1;
} 

if team==TEAM_RED sprite_index=BackstabTorsoRedS;
else sprite_index=BackstabTorsoBlueS;

x=owner.x;
y=owner.y;

if image_index >= 32 {
    done=1;
    image_speed=0;
}

if(not done) {
    if(alpha<0.99) {
        alpha = power(alpha,0.7);
    } else {
        alpha = 0.99;
    }
} else {
    if(alpha>0.01) {
        alpha = power(alpha,1/0.7);
    } else {
        instance_destroy();
    }
}
