arguments.argument:
{
    if(readyToShoot and bubbleCount < maxBubbles)
    {
        var shot;
        randomize();
        shot = instance_create(x+lengthdir_x(8, owner.aimDirection), y+lengthdir_y(8, owner.aimDirection), Bubble);
        shot.direction = owner.aimDirection;
        shot.speed = 10;
        shot.owner = owner;
        shot.ownerPlayer = ownerPlayer;
        shot.team = owner.team;
        shot.gun = id;
        shot.weapon = WEAPON_BUBBLE;
        with(shot)
        {
            motion_add(owner.direction, owner.speed);
        }
        justShot = true;
        readyToShoot = false;
        alarm[0] = bubbleRefireTime;
        bubbleCount += 1;
    }
}
