arguments.argument:
if(uberReady){
    if(alarm[4]<=0) {
        loopsoundstart(x,y,UberIdleSnd);
    } else {
        loopsoundmaintain(x,y,UberIdleSnd);
    }
    alarm[4]=2;
}

if(ubering) {
    owner.ubered = 1;
    owner.alarm[2]=3;
    uberCharge -= 10;
    if(uberCharge<=0) {
        uberCharge = 0;
        ubering = false;
    }
}
